import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Form = () => {
  const [recipe, setRecipe] = useState({
    name: '',
    origin: '',
    mainIngredients: '',
    image: ''
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRecipe((prevRecipe) => ({
      ...prevRecipe,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/recipes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: recipe.name,
          origin: recipe.origin,
          mainIngredients: recipe.mainIngredients.split(',').map(ingredient => ingredient.trim()), // Convert comma-separated string to array
          image: recipe.image,
        }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      // Reset form fields
      setRecipe({
        name: '',
        origin: '',
        mainIngredients: '',
        image: ''
      });

      // Navigate back to the home page
      navigate('/');
    } catch (error) {
      console.error('Error adding recipe:', error);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Add a New Recipe</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={recipe.name}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label htmlFor="origin">Origin:</label>
          <input
            type="text"
            id="origin"
            name="origin"
            value={recipe.origin}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label htmlFor="mainIngredients">Main Ingredients (comma-separated):</label>
          <input
            type="text"
            id="mainIngredients"
            name="mainIngredients"
            value={recipe.mainIngredients}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label htmlFor="image">Image URL:</label>
          <input
            type="url"
            id="image"
            name="image"
            value={recipe.image}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit">Add Recipe</button>
      </form>
    </div>
  );
};

export default Form;
