import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Form = () => {
  const [recipe, setRecipe] = useState({
    id: '',
    name: '',
    origin: '',
    mainIngredients: '',
    image: ''
  });

  const navigate = useNavigate();


  const handleChange = (e) => {
    const { name, value } = e.target;
    setRecipe((prevRecipe) => ({
      ...prevRecipe,
      [name]: value
    }));
  };


  const handleSubmit = (e) => {
    e.preventDefault();

    setRecipe({
      id: '',
      name: '',
      origin: '',
      mainIngredients: '',
      image: ''
    });


    navigate('/');
  };


  const handleCombinedInputChange = (e) => {
    const [id, name] = e.target.value.split(':').map(item => item.trim());
    setRecipe((prevRecipe) => ({
      ...prevRecipe,
      id: id || '',
      name: name || ''
    }));
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Add a New Recipe</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="idName">ID and Name (format: ID:Name):</label>
          <input
            type="text"
            id="idName"
            name="idName"
            placeholder="e.g., 1: Spaghetti Carbonara"
            value={`${recipe.id}:${recipe.name}`}
            onChange={handleCombinedInputChange}
            required
          />
        </div>

        <div>
          <label htmlFor="origin">Origin:</label>
          <input
            type="text"
            id="origin"
            name="origin"
            value={recipe.origin}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label htmlFor="mainIngredients">Main Ingredients (comma-separated):</label>
          <input
            type="text"
            id="mainIngredients"
            name="mainIngredients"
            value={recipe.mainIngredients}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label htmlFor="image">Image URL:</label>
          <input
            type="url"
            id="image"
            name="image"
            value={recipe.image}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit">Add Recipe</button>
      </form>
    </div>
  );
};

export default Form;
